import React, { useState, useEffect } from 'react';
import './ImagePoster.css'; // Importing the CSS file

// List of image URLs (can be local or from a CDN)
const imageUrls = [
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRMiDxYQWI7tga5pJ19dqWfZrhzaOWiwTjCtdsvmIy-tGW4oLhEMLcI5yTXynK6pDbwmss&usqp=CAU",
  "data:image/jpeg;base64,",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ5FyV8tdOgPKgQxUIe8xEVXd3dmL7dWBoMEQ&s"
];

const ImagePoster = () => {
  const [currentImage, setCurrentImage] = useState(0); // State to keep track of the current image

  // Effect to change image every 1 second
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage + 1) % imageUrls.length); // Cycle through images
    }, 1000); // 1000ms = 1 second

    return () => clearInterval(interval); // Cleanup interval on component unmount
  }, []);

  return (
    <div className="poster-container">
      <img src={imageUrls[currentImage]} alt="Poster" className="poster-image" />
    </div>
  );
};

export default ImagePoster;
